mapper.insert:

	SELECT NVL(MAX(STORY_NUM),0)+1 AS "STORY_NUM" FROM SNS_STORY

	INSERT INTO SNS_STORY(STORY_NUM, STORY_CONTENT, STORY_DATE, STORY_PHOTO, USER_EMAIL)
	VALUES
		(
			#{storyNum}
		   ,#{storyContent}
		   ,SYSDATE
		   
		   ,#{storyPhoto}
		   
		   
		   ,null
		   
		   ,#{userEmail}
		 )

mapper.delete:
DELETE SNS_STORY
WHERE STORY_NUM = #{storyNum}
    AND USER_EMAIL = #{userEmail}
